select sum(c."AL") as "AL", round( avg(c."perCustomerAccAL") :: NUMERIC, 2 ) as "perCustomerAccAL", round( avg(c."percentAL") :: NUMERIC, 4 ) as "percentAL", round( avg(c."ALI") :: NUMERIC, 4 ) as "ALI", round( avg(c."percentNonMeterRead") :: NUMERIC, 4 ) as "percentNonMeterRead", round( avg(c."meterReadRate") :: NUMERIC, 4 ) as "meterReadRate", round( avg(c."nonMeterReadTimeRate") :: NUMERIC, 4 ) as "nonMeterReadTimeRate", round( avg(c."overdueMetersRate") :: NUMERIC, 4 ) as "overdueMetersRate", round( avg(c."nonBasicInfoMeterRate") :: NUMERIC, 4 ) as "nonBasicInfoMeterRate" FROM ( select
