b."zoneNo" as "zoneNo", sum(case when b.code='SLMAL' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "AL", sum(case when b.code='SLMALCA' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "perCustomerAccAL", sum(case when b.code='SLMALR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentAL", sum(case when b.code='SLMALI' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "ALI", sum(case when b.code='SLMNMRR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentNonMeterRead", sum(case when b.code='SLMTROMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "meterReadRate", sum(case when b.code='SLMIMRTR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonMeterReadTimeRate", sum(case when b.code='SLMOSMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "overdueMetersRate", sum(case when b.code='SLMIBIOMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonBasicInfoMeterRate", b."yearMonth" as time
