select code as "meterNo",round( max(flux/day/24*2) :: NUMERIC, 6 ) as qh
from "gdh_month_flux_C001_jc" 
where month >= #{qaDTO.startTime} || '' and month  <= #{qaDTO.endTime} || ''

	and code in 

		#{item.meterNo}    


group by code;

select month as date,sum(flux) as value
from "gdh_month_flux_C001_jc" 
where month >= #{qaDTO.startTime} || '' and month  <= #{qaDTO.endTime} || ''

	and code in 


			#{item.meterNo} 



group by month order by month ASC;

select code from "gdh_month_flux_C001_jc" where month >= #{startTime} || '' and month <= #{endTime} || ''
and code in
#{item}
and flux = 0.0 group by code;

	select round( sum(flux) :: NUMERIC, 2 ) as mFlow
	from "gdh_month_flux_C001_jc" where
	month = #{time} || ''

		and code in 

			#{item}    


    group by month;

	select code,month,flux
	from "gdh_month_flux_C001_jc" where
	month >= #{startTime} || '' and month  <= #{endTime} || ''

		and code in 

			#{item}    


    order by code,month asc;

select code,month,flux from "gdh_month_flux_C001_jc" where month >= #{startTime} || '' and month <= #{endTime} || '' and flux = 0
and code in
#{item}
order by code;

select code, round( avg(flux) :: NUMERIC, 2 ) as flux from "gdh_month_flux_C001_jc" where month >= #{startTime} || '' and month <= #{endTime} || ''
and code in
#{item}
group by code;

select round( max(flux) :: NUMERIC, 2 ) as flux from "gdh_month_flux_C001_jc" where month >= #{startTime} || '' and month <= #{endTime} || ''
and code in
#{item}
group by code;

select code as meterNo, avg(flux)-3*stddev(flux) as minV, avg(flux)+3*stddev(flux) as maxV from "gdh_month_flux_C001_jc" where month >= #{startTime} || '' and month <= #{endTime} || ''
and code in
#{item}
group by code;
