select sum(c."AL") as "AL", round( avg(c."perCustomerAccAL") :: NUMERIC, 2 ) as "perCustomerAccAL", round( avg(c."percentAL") :: NUMERIC, 4 ) as "percentAL", round( avg(c."ALI") :: NUMERIC, 4 ) as "ALI", round( avg(c."percentNonMeterRead") :: NUMERIC, 4 ) as "percentNonMeterRead", round( avg(c."meterReadRate") :: NUMERIC, 4 ) as "meterReadRate", round( avg(c."nonMeterReadTimeRate") :: NUMERIC, 4 ) as "nonMeterReadTimeRate", round( avg(c."overdueMetersRate") :: NUMERIC, 4 ) as "overdueMetersRate", round( avg(c."nonBasicInfoMeterRate") :: NUMERIC, 4 ) as "nonBasicInfoMeterRate" FROM ( select
sum(case when b.code='DMYAL' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "AL", sum(case when b.code='DMYALCA' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "perCustomerAccAL", sum(case when b.code='DMYALR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentAL", sum(case when b.code='DMYALI' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "ALI", sum(case when b.code='DMYNMRR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentNonMeterRead", sum(case when b.code='DMYTROMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "meterReadRate", sum(case when b.code='DMYIMRTR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonMeterReadTimeRate", sum(case when b.code='DMYOSMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "overdueMetersRate", sum(case when b.code='DMYIBIOMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonBasicInfoMeterRate", b.year as time
sum(case when b.code='SLYAL' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "AL", sum(case when b.code='SLYALCA' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "perCustomerAccAL", sum(case when b.code='SLYALR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentAL", sum(case when b.code='SLYALI' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "ALI", sum(case when b.code='SLYNMRR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentNonMeterRead", sum(case when b.code='SLYTROMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "meterReadRate", sum(case when b.code='SLYIMRTR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonMeterReadTimeRate", sum(case when b.code='SLYOSMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "overdueMetersRate", sum(case when b.code='SLYIBIOMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonBasicInfoMeterRate", b.year as time
sum(case when b.code='FLYAL' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "AL", sum(case when b.code='FLYALCA' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "perCustomerAccAL", sum(case when b.code='FLYALR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentAL", sum(case when b.code='FLYALI' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "ALI", sum(case when b.code='FLYNMRR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentNonMeterRead", sum(case when b.code='FLYTROMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "meterReadRate", sum(case when b.code='FLYIMRTR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonMeterReadTimeRate", sum(case when b.code='FLYOSMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "overdueMetersRate", sum(case when b.code='FLYIBIOMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonBasicInfoMeterRate", b.year as time
from (select a.id,a.year,a.code,a.value,ind.precision from ( select id,year,code,value from ana_apparent_year where year >= #{startTime} and year <= #{endTime}
and "zoneNo" = #{zoneNo}
) a,lc_app_dim_indicator ind where ind.code = a.code ) b group by b.year) c

