b."zoneNo" as "zoneNo", sum(case when b.code='FLMAL' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "AL", sum(case when b.code='FLMALCA' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "perCustomerAccAL", sum(case when b.code='FLMALR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentAL", sum(case when b.code='FLMALI' then round( value/b.precision :: NUMERIC, 2 ) else 0 end) as "ALI", sum(case when b.code='FLMNMRR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "percentNonMeterRead", sum(case when b.code='FLMTROMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "meterReadRate", sum(case when b.code='FLMIMRTR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonMeterReadTimeRate", sum(case when b.code='FLMOSMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "overdueMetersRate", sum(case when b.code='FLMIBIOMR' then round( value/b.precision :: NUMERIC, 4 ) else 0 end) as "nonBasicInfoMeterRate"
