select month as date,sum(flux) as value
from gdh_month_flux_c001_jc 
where month >= #{qaDTO.startTime} || '' and month  <= #{qaDTO.endTime} || ''

	and code in 


			#{item.meterNo} 



group by month order by month ASC;

select round( max(flux) :: NUMERIC, 2 ) as flux from gdh_month_flux_c001_jc where month >= #{qaDTO.startTime} || '' and month <= #{qaDTO.endTime} || ''
and code in
#{item}
group by code;

select "CTM_NUM" as code,"MONTH_ID" as month,"REAL_NUM" as flux
from rw_fct_cb where 
"MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID"  <= #{qaDTO.endTime}

	and "CTM_NUM" in 

		#{item.accNo}

	select "CTM_NUM" as accNo,
	avg("REAL_NUM")-3*stddev("REAL_NUM") as minV,
	avg("REAL_NUM")+3*stddev("REAL_NUM") as maxV
	from rw_fct_cb where
	"MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID"  <= #{qaDTO.endTime}

		and "CTM_NUM" in 

			#{item.accNo}    


    group by "CTM_NUM";

select round( sum("REAL_NUM")/100 :: NUMERIC, 2 ) as mFlow
from rw_fct_cb where
"MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID"  <= #{qaDTO.endTime}

	and "CTM_NUM" in 

		#{item}

select a."CTM_NUM" as code,b."MONTH_ID" as month,b."REAL_NUM" as flux from rw_fct_ctm a INNER JOIN (
select "CTM_NUM","MONTH_ID" ,"REAL_NUM"
from rw_fct_cb where
"MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID"  <= #{qaDTO.endTime}
and "REAL_NUM" <= 0
) b on a."CTM_NUM" = b."CTM_NUM" and a."BORE_VALUE" < '50'

select b."CTM_NUM" as code,avg(b."REAL_NUM") as flux,a."BORE_VALUE" as meterDn from rw_fct_ctm a INNER JOIN (
select "CTM_NUM","MONTH_ID" ,"REAL_NUM"
from rw_fct_cb where
"MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID"  <= #{qaDTO.endTime}
) b on a."CTM_NUM" = b."CTM_NUM" and a."BORE_VALUE" < '50' group by b."CTM_NUM",a."BORE_VALUE"

select round( sum(b."REAL_NUM")/100 :: NUMERIC, 2 ) as flux from rw_fct_ctm a INNER JOIN (
select "CTM_NUM","MONTH_ID" ,"REAL_NUM"
from rw_fct_cb where
"MONTH_ID" >= #{startTime} and "MONTH_ID"  <= #{endTime}
) b on a."CTM_NUM" = b."CTM_NUM" and a."BORE_VALUE"  >= '50'

select round( sum(b."REAL_NUM")/100 :: NUMERIC, 2 ) as flux from rw_fct_ctm a INNER JOIN (
select "CTM_NUM","MONTH_ID" ,"REAL_NUM"
from rw_fct_cb where
"MONTH_ID" >= #{startTime} and "MONTH_ID"  <= #{endTime}
) b on a."CTM_NUM" = b."CTM_NUM" and a."BORE_VALUE"  < '50'

select count(*) from (
select c."CTM_NUM" as accNo,c."BORE_VALUE" as meterDn,c."CTM_NAME" as accName,c."TBL_NUM" as meterNo,c."CTM_ADDR" as address,c."YS_NAME" as useType ,c."CB_DAY" as mReadDate,max(c.real_num) as qh from (
select a."CTM_NUM",a."BORE_VALUE",a."CTM_NAME",a."TBL_NUM",a."CTM_ADDR",a."YS_NAME",a."CB_DAY",b.real_num from rw_fct_ctm a INNER JOIN (
	select round( "REAL_NUM"/30/24*2 :: NUMERIC, 6 ) as real_num,"CTM_NUM" from rw_fct_cb
	where "MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID" <= #{qaDTO.endTime}
) b on a."CTM_NUM" = b."CTM_NUM" and a."BORE_VALUE" >= '50') c group by c."CTM_NUM",c."BORE_VALUE",c."CTM_NAME",c."TBL_NUM",c."CTM_ADDR",c."YS_NAME",c."CB_DAY") d where d.qh < 0.001

select count(*)
from (
select c."CTM_NUM" as accNo,c."BORE_VALUE" as meterDn,c."CTM_NAME" as accName,c."TBL_NUM" as meterNo,c."CTM_ADDR" as address,c."YS_NAME" as useType ,c."CB_DAY" as mReadDate,c."CLASS_ID" as fsMeterStatus,max(c.real_num) as qh from (
select a."CTM_NUM",a."BORE_VALUE",a."CTM_NAME",a."TBL_NUM",a."CTM_ADDR",a."YS_NAME",a."CB_DAY",
a."CLASS_ID",b.real_num from rw_fct_ctm a INNER JOIN (
	select round( "REAL_NUM"/30/24*2 :: NUMERIC, 6 ) as real_num,"CTM_NUM" from rw_fct_cb 
	where "MONTH_ID" >= #{qaDTO.startTime} and "MONTH_ID" <= #{qaDTO.endTime}
) b on a."CTM_NUM" = b."CTM_NUM" and a."BORE_VALUE" >= '50') c group by c."CTM_NUM",c."BORE_VALUE",c."CTM_NAME",c."TBL_NUM",c."CTM_ADDR",c."YS_NAME",c."CB_DAY",c."CLASS_ID") d 
where d.qh < (select cast(value as numeric) from sm_config where key = 'QMIDN' || d.meterDn) or d.qh >= (select cast(value as numeric) from sm_config where key = 'QMADN' || d.meterDn)

