mapper.select:
select * from( select 0 as rows,reportdate,reporttime,type,row,integrity,accuracy,available,timely,consistence,rating from app_mon_report where 1=1
and reportdate=#{mon}
limit #{pageCount} OFFSET #{pageCount}*(#{page}-1)
)t union all select count(*) as rows,'' ,'','',0,0,0,0,0,0,0 from app_mon_report where 1=1
and reportdate=#{mon}
