mapper.select:
select p_code as zoneNo, name as zoneName,inputdate as createTime from gis_exist_zone
where 1 = 1

	and rank = #{rank}


	and p_code = #{zoneNo}

select p_code as zoneNo, name as zoneName,inputdate as createTime from gis_exist_zone where rank = #{rank}
and p_code like '%'||#{zoneNo}||'%'
and name like '%'||#{zoneName}||'%'

select "pCode" as zoneNo, name as zoneName from gis_exist_vzone where "vType" = #{zoneType}
and "pCode" like '%'||#{zoneNo}||'%'
and name like '%'||#{zoneName}||'%'
