mapper.select:
select c.id as id, c."pointCode" as "pointCode", c.code as code, c."schemeCode" as "schemeCode", c."alarmType" as "alarmType", c."objectCode" as "objectCode", c."areaCode" as "areaCode",
c."objectType" as "objectType", c."createTime" as "createTime", c.content as content, c."createBy" as "createBy", c."alarmTime" as "alarmTime", c."updateBy" as "updateBy",
c."updateTime" as "updateTime", d."code" as "taskCode", d.state as "taskState", d."executorCode" as "executorCode" from
(select a.id as id, a."pointCode" as "pointCode", a.code as code, a."schemeCode" as "schemeCode", a."alarmType" as "alarmType", a."objectCode" as "objectCode", a."areaCode" as "areaCode",
b."objectType" as "objectType", a."createTime" as "createTime", a.content as content, a."createBy" as "createBy", a."alarmTime" as "alarmTime", a."updateBy" as "updateBy",
a."updateTime" as "updateTime" from
(select * from app_warninginf where "alarmTime" between '${startTime}' and '${endTime}' 
 and "alarmType" = #{alarmType} 
 and "areaCode" = #{areaCode}
 and "objectType" = #{objectType}
 and "objectCode" like '%'||#{objectCode}||'%'
 order by ${orderby}) a inner join app_warningscheme b on a."schemeCode" = b.code) c inner join app_warningprocessing d on c.code = d."warningCode"
limit #{pageCount} OFFSET #{pageCount}*(#{page}-1);

select count(*) from (select a.id as id, a."pointCode" as "pointCode", a.code as code, a."schemeCode" as "schemeCode", a."alarmType" as "alarmType", a."objectCode" as "objectCode", a."areaCode" as "areaCode",
b."objectType" as "objectType", a."createTime" as "createTime", a.content as content, a."createBy" as "createBy", a."alarmTime" as "alarmTime", a."updateBy" as "updateBy",
a."updateTime" as "updateTime" from (select * from app_warninginf where "alarmTime" between '${startTime}' and '${endTime}' 
 and "alarmType" = #{alarmType} 
 and "areaCode" = #{areaCode}
 and "objectType" = #{objectType}
 and "objectCode" like '%'||#{objectCode}||'%'
) a inner join app_warningscheme b on a."schemeCode" = b.code) c inner join app_warningprocessing d on c.code = d."warningCode"

select a.code as code, b."alarmType" as "alarmType", a."alarmTime" as "alarmTime" from app_warninginf a inner join app_warningscheme b on a."schemeCode" = b.code

  b."objectType" = #{objectType} and 
  a.objectCode = #{objectCode}
