mapper.select:
select * from "APP_eventInfo" where "createTime" between '${startTime}' and '${endTime}'
 and "objectType" = #{objectType}
 and "type" = #{type}
 and "subtype" = #{subtype}
 and "objectCode" = #{objectCode}
 and "firstPartition" = #{firstPartition}

mapper.update:
update "APP_eventInfo"

 type = #{type}
 subtype = #{subtype}
 "objectCode" = #{objectCode}
 "firstPartition" = #{firstPartition}
 "secondPartition" = #{secondPartition}
 "dmaCode" = #{dmaCode}
 "happenTime" = #{happenTime}
 content = #{content}

where code = #{code}
