mapper.select:
select "user".id as "userId","user"."loginName",case when "user".phone is null then '' else "user".phone end,case when "user"."email" is null then '' else "user"."email" end,"user".name,"user".position,case when "user".sex is null then -1 else "user".sex end,"user".photo,"user"."openID","user".status,"user"."workNo", "user"."whetUse","user"."createBy","user"."createTime",case when string_agg(distinct("dept".name),';') is null then '' else string_agg(distinct("dept".name),';') end as "depname","user".code as code from "SM_user" as "user" left join "SM_userDept" as "userdept" on "userdept"."userCode"="user".Code left join "SM_department" as "dept" on "dept".code="userdept"."deptCode"
and "user".name like '%'||#{name}||'%' and "dept".name like '%'||#{depName}||'%' and "user".id ='${userId}' and "user".status ='${status}' and "user"."whetUse" ='${whetUse}' and "user".code = #{code} and "user"."loginName" = #{loginName} and "user"."workNo" = #{workNo} and "user"."code" = #{code}
group by "user".id ,"user"."loginName","user".phone,"user"."email", "user".name,"user".position,"user".sex,"user"."openID","user".status,"user"."workNo", "user"."whetUse", "user"."createBy","user"."createTime" order by "user"."createTime" desc limit #{pageCount} OFFSET #{pageCount}*(#{page}-1);

mapper.update:
update "SM_user"
name = #{name}, "loginName" = #{loginName}, sex = #{sex,jdbcType=INTEGER}, "email" = #{email}, "updateBy" = #{updateBy}, "updateTime" = Now(), status = #{status},
where code=#{code}

mapper.select:
select id
from "SM_userDept"
where "deptCode"=#{depCode} and "userCode"=#{userCode}

select "role".name as "roleName","role".code as "roleCode"
from  "SM_userRole" as "role"
where "role"."name"=#{roleName}

mapper.update:
update "SM_userRole"
name= #{roleName}, remark= #{roleName}, "updateBy" = #{updateBy}, "updateTime" = Now(),
where code=#{roleCode}

mapper.select:
select code as "roleCode",name as "roleName"
from "SM_userRole"
where   "code" =  #{roleCode}

select "userCode","roleCode"
from "SM_userRoleRelation"
where "userCode" =   #{userCode}
and  "roleCode" =  #{roleCode}

select "user".id as "userId","user"."loginName",case when "user".phone is null then '' else "user".phone end,"user"."email","user".name,"user".position,"user".sex,"user".photo,"user"."openID","user".status,"user"."workNo", "user"."whetUse","user"."createBy","user"."createTime",case when string_agg(distinct("dept".name),';') is null then '' else string_agg(distinct("dept".name),';') end as "depname","user".code as code from "SM_user" as "user" left join "SM_userDept" as "userdept" on "userdept"."userCode"="user".code left join "SM_department" as "dept" on "dept".code="userdept"."deptCode"
and "user"."loginName" like '%'||#{loginName}||'%' and "user"."name" like '%'||#{name}||'%' and "user".code not in(select "userrela"."userCode" from "SM_userRoleRelation" as "userrela" where "userrela"."roleCode" =#{roleCode}) and "user"."whetUse"=#{whetUse}
group by "user".id ,"user"."loginName","user".phone,"user"."email", "user".name,"user".position,"user".sex,"user"."openID","user".status,"user"."workNo", "user"."whetUse", "user"."createBy","user"."createTime" order by "user"."createTime" desc limit #{pageCount} OFFSET #{pageCount}*(#{page}-1);

select "user".id as "userId","user"."loginName",case when "user".phone is null then '' else "user".phone end,"user"."email","user".name,"user".position,"user".sex,"user".photo,"user"."openID","user".status,"user"."workNo", "user"."whetUse","user"."createBy","user"."createTime",case when string_agg(distinct("dept".name),';') is null then '' else string_agg(distinct("dept".name),';') end as "depname","user".code from "SM_user" as "user" left join "SM_userDept" as "userdept" on "userdept"."userCode"="user".code left join "SM_department" as "dept" on "dept".code="userdept"."deptCode"
and "user"."loginName" like '%'||#{loginName}||'%' and "user"."name" like '%'||#{name}||'%' and "user".code not in(select "deptuser"."userCode" from "SM_userDept" as "deptuser" where "deptuser"."deptCode" =#{depCode}) and "user"."whetUse"=#{whetUse}
group by "user".id ,"user"."loginName","user".phone,"user"."email", "user".name,"user".position,"user".sex,"user"."openID","user".status,"user"."workNo", "user"."whetUse", "user"."createBy","user"."createTime" order by "user"."createTime" desc limit #{pageCount} OFFSET #{pageCount}*(#{page}-1);

select code as "depCode",name as "depName"
from "SM_department"
where   "code" =  #{depCode}

select "userCode","deptCode"
from "SM_userDept"
where "userCode" =   #{userCode}
and  "deptCode" =  #{depCode}

          select "dic".id as dicId,"dic".parent as dicParent,"dic".cn as "dicCn","dic".en as "dicEn","dic".tc as "dicTc","dic".remark as dicRemark,"dic".key as dicKey,"dic".value as dicValue,"dic".seq as dicSeq,"dic"."createBy" as createBy,"dic"."createTime" as createTime,"dic"."updateBy" as updateBy,"dic"."updateTime" as updateTime 
          ,"dic"."enValue" as "dicEnValue","dic"."tcValue" as "dicTcValue","dic"."detRemark" as "dicDetRemark","dic".type as "dicType"
          from "SM_dataDictionary" as "dic"

	         and "dic".id = '${dicId}'
			 and "dic".parent =#{dicParent}  
	   	     and "dic".en like  '%'||#{dicEn}||'%' or "dic".cn like '%'||#{dicEn}||'%' or "dic".tc like '%'||#{dicEn}||'%'	   	
	   	     and "dic".remark ='${dicRemark}'  
	   	     and "dic".key =#{dicKey}
	   	     and "dic"."value" =#{dicValue}  
	   	     and "dic"."enValue" =#{dicEnValue}  
	   	     and "dic"."tcValue" =#{dicTcValue}       

		  order by "dic".seq

select distinct "dic".parent as dicParent,"dic".cn as "dicCn","dic".en as "dicEn","dic".tc as "dicTc","dic".remark as dicRemark,"dic".type as "dicType" from "SM_dataDictionary" as "dic"
and "dic".id = '${dicId}' and "dic".parent like '%'||#{dicParent}||'%' and "dic".en like '%'||#{dicEn}||'%' or "dic".cn like '%'||#{dicEn}||'%' or "dic".tc like '%'||#{dicEn}||'%' and "dic".remark =#{dicRemark} and "dic".key =#{dicKey} and "dic"."value" =#{dicValue}
group by "dic".parent ,"dic".cn ,"dic".en ,"dic".tc,"dic".remark,"dic".type order by "dic"."parent" desc limit #{pageCount} OFFSET #{pageCount}*(#{page}-1);

select count(*) from (select distinct "dic".parent as dicParent,"dic".cn as "dicCn","dic".en as "dicEn","dic".tc as "dicTc","dic".remark as dicRemark,"dic".type as "dicType" from "SM_dataDictionary" as "dic"
and "dic".id = '${dicId}' and "dic".parent like '%'||#{dicParent}||'%' and "dic".en like '%'||#{dicEn}||'%' or "dic".cn like '%'||#{dicEn}||'%' or "dic".tc like '%'||#{dicEn}||'%' and "dic".remark =#{dicRemark} and "dic".key =#{dicKey} and "dic"."value" =#{dicValue}
group by "dic".parent ,"dic".cn ,"dic".en,"dic".tc ,"dic".remark,"dic".type
) as "dic"

