select "table"."code" as "tableMapperCode","table"."confCode" as "configCode","table"."otherTabName" as "otherTabName", "table"."otherTabCode" as "otherTabCode","table"."tableName" as "tableName","table"."tableCode" as "tableCode", "table"."createTime" as "createTime","table"."updateTime" as "updateTime","table"."createBy" as "createBy", "table"."updateBy" as "updateBy" from "SM_tableMapper" as "table"
